    public function getCurrentGasPrices(){
        
        $response = json_decode(@file_get_contents('https://ethgasstation.info/json/ethgasAPI.json'), true);

        if ($response === false || $response === null) {
            throw new \Exception('Failed fetch to gas info from `https://ethgasstation.info/json/ethgasAPI.json` in `getCurrentGasPrices()`.');
        }

        $prices = [
            "low"=> $response['safeLow'] / 10,
            "medium"=> $response['average'] / 10,
            "high"=> $response['fast'] / 10
        ];
        
        return $prices;
    }

