    public function getChainParamValue($chainParams, $key) {
        if (is_array($chainParams)) {
            foreach($chainParams as $chainParam) {
                if ($chainParam['key'] == $key) {
                    return $chainParam['value'];
                }
            }
        }
        
        return false;
    }

    public function base58_encode($string)
    {
        $alphabet = '123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz';
        $base = strlen($alphabet);
        if (is_string($string) === false) {
            return false;
        }
        if (strlen($string) === 0) {
            return '';
        }
        $bytes = array_values(unpack('C*', $string));
        $decimal = $bytes[0];
        for ($i = 1, $l = count($bytes); $i < $l; $i++) {
            $decimal = bcmul($decimal, 256);
            $decimal = bcadd($decimal, $bytes[$i]);
        }
        $output = '';
        while ($decimal >= $base) {
            $div = bcdiv($decimal, $base, 0);
            $mod = bcmod($decimal, $base);
            $output .= $alphabet[$mod];
            $decimal = $div;
        }
        if ($decimal > 0) {
            $output .= $alphabet[$decimal];
        }
        $output = strrev($output);
        foreach ($bytes as $byte) {
            if ($byte === 0) {
                $output = $alphabet[0] . $output;
                continue;
            }
            break;
        }
        return (string) $output;
    }

    public function base58_decode($base58)
    {
        $alphabet = '123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz';
        $base = strlen($alphabet);
        if (is_string($base58) === false) {
            return false;
        }
        if (strlen($base58) === 0) {
            return '';
        }
        $indexes = array_flip(str_split($alphabet));
        $chars = str_split($base58);
        foreach ($chars as $char) {
            if (isset($indexes[$char]) === false) {
                return false;
            }
        }
        $decimal = $indexes[$chars[0]];
        for ($i = 1, $l = count($chars); $i < $l; $i++) {
            $decimal = bcmul($decimal, $base);
            $decimal = bcadd($decimal, $indexes[$chars[$i]]);
        }
        $output = '';
        while ($decimal > 0) {
            $byte = bcmod($decimal, 256);
            $output = pack('C', $byte) . $output;
            $decimal = bcdiv($decimal, 256, 0);
        }
        foreach ($chars as $char) {
            if ($indexes[$char] === 0) {
                $output = "\x00" . $output;
                continue;
            }
            break;
        }

        return $output;
    }
    
    //encode address from byte[] to base58check string
    public function base58check_en($address)
    {
         $hash0 = hash("sha256", $address);
         $hash1 = hash("sha256", hex2bin($hash0));
         $checksum = substr($hash1, 0, 8);
         $address = $address.hex2bin($checksum);
         $base58add = $this->base58_encode($address);
         return $base58add;
    }

    //decode address from base58check string to byte[]
    public function base58check_de($base58add)
    {
         $address = $this->base58_decode($base58add);
         $size = strlen($address);
         if ($size != 25) {
            return false;
         }
         $checksum = substr($address, 21);
         $address = substr($address, 0, 21);     
         $hash0 = hash("sha256", $address);
         $hash1 = hash("sha256", hex2bin($hash0));
         $checksum0 = substr($hash1, 0, 8);
         $checksum1 = bin2hex($checksum);
         if (strcmp($checksum0, $checksum1)) {
            return false;
         }

         return $address;
    }

    public function hexString2Base58check($hexString){
        $address = hex2bin($hexString);
        $base58add = $this->base58check_en($address);
        return $base58add;
    }

    public function base58check2HexString($base58add){
        $address = $this->base58check_de($base58add);
        $hexString = bin2hex($address);
        return $hexString;
    }

