        $address = base64_decode($base64);
        $base58add = $this->base58check_en($address);
        return $base58add;
    }

    public function hex2Dec(string $hex): string
    {
        $dec = 0;
        $len = strlen($hex);
        for ($i = 1; $i <= $len; $i++) {
            $dec = bcadd($dec, bcmul(strval(hexdec($hex[$i - 1])), bcpow('16', strval($len - $i))));
        }
        return $dec;
    }

    public function dec2Hex($dec): string
    {
        $last = bcmod($dec, 16);
        $remain = bcdiv(bcsub($dec, $last), 16);
        if ($remain == 0) {
            return dechex($last);
        } else {
            return $this->dec2Hex($remain) . dechex($last);
        }
    }
        
    public function hex2Str(string $hex): string
    {
        $str = "";
        for ($i = 0; $i < strlen($hex) - 1; $i += 2) {
            $str .= chr(hexdec($hex[$i] . $hex[$i + 1]));
        }
        return $str;
    }
        
    public function str2Hex(string $str): string
    {
        $hex = "";
        for ($i = 0; $i < strlen($str); $i++) {    
            $hex .= str_pad( dechex(ord($str[$i])) , 2 ,"0", STR_PAD_LEFT);
        }
        return $hex;
    }

